import React,{useState} from 'react';
import { Text, View, TextInput, TouchableOpacity ,ToastAndroid} from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { styles } from '../style';
//firebase
import { authentication} from '../firebase/firebase-config';
import { createUserWithEmailAndPassword } from 'firebase/auth';

 
export default function RegisterScreen({navigation}) {
  
  //const [isSignedIn, setIsSignedIn] = useState(false);
  //text input states
  const [email,setEmail] = useState('');
  const [password,setPassword] = useState('');


  /**
   * FIREBASE REGISTRATION
  */
  const RegisterUser = ()=>{
    console.log("trying to register");
    createUserWithEmailAndPassword(authentication, email, password)
      .then((userCredential) => {
        // Signed in 
        const user = userCredential.user;
        // ...
      })
      .catch((error) => {
        console.log("fail");
        const errorCode = error.code;
        const errorMessage = error.message;
        // ..
      });
  }

  return (
    <SafeAreaView style={styles.appScreen}>

      <View style={styles.formView}>

        <TextInput style={styles.formInput} placeholder='email'
            value={email} onChangeText={text=>setEmail(text)} />

        <TextInput style={styles.formInput} placeholder='password' 
            secureTextEntry={false} value={password}
            onChangeText={text=>setPassword(text)} />

        <TouchableOpacity style={styles.formBtn} onPress={RegisterUser}>
          <Text style={styles.formBtnText}>Google Register</Text>
        </TouchableOpacity>

      </View>

    </SafeAreaView>
  );

}
