import React,{useState} from 'react';
import { Text, View, TextInput,TouchableOpacity } from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { styles } from '../style';
//firebase
import { authentication } from '../firebase/firebase-config';
import { signInWithEmailAndPassword } from 'firebase/auth';


export default function LoginScreen({navigation}) {

  //text input states
  const [email,setEmail] = useState('');
  const [password,setPassword] = useState('');


  /**
   * FIREBASE LOGIN SOME REDIRECTION INIT
  **/
  const SignInUser = () =>{
    console.log("...");
    signInWithEmailAndPassword(authentication, email, password)
      .then((userCredential) => {
        // Signed in 
        console.log('logged');
        navigation.navigate('RegisterScreen');
        const user = userCredential.user;
        // ...
      })
      .catch((error) => {
        console.log("unlog");
        const errorCode = error.code;
        const errorMessage = error.message;
        console.log(errorMessage);
      });
  }


  return (
    <SafeAreaView style={styles.appScreen}>

      <View style={styles.formView}>
        <TextInput style={styles.formInput} placeholder='email'
            value={email} 
            onChangeText={text=>setEmail(text)} />

        <TextInput style={styles.formInput} placeholder='password'
            secureTextEntry={false} value={password}
            onChangeText={text=>setPassword(text)} />

        <TouchableOpacity style={styles.formBtn} onPress={SignInUser}>
          <Text style={styles.formBtnText}>Google Login</Text>
        </TouchableOpacity>
      </View>

    </SafeAreaView>
  );

}
