import React,{useState} from 'react';
import { Text, View, ScrollView,TextInput,TouchableOpacity } from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { styles } from '.././style';
//firestore 
import { db } from '../firebase/firebase-config';
import { onSnapshot,collection,setDoc,doc} from 'firebase/firestore';

 
export default function DetailScreen() {

  /** make details about the customers */
  const [email,setEmail] = useState('');
  const [password,setPassword] = useState('');
  const [firstName,setFirstName] = useState('');
  const [lastName,setLastName] = useState('');
  const [contact,setContact] = useState('');
  const [address,setAddress] = useState('');


  const SendData = async()=>{
    console.log("...");
    const fireRef = doc(db,`detail/client`);
    const docData ={
        firstName:firstName,
        lastName:lastName,
        contact:contact,
        address:address
    };
    try{
        await setDoc(fireRef, docData, { merge:true });
        console.log(".*");
    } catch(error) {
        console.log(`#. ${error}`);
    }
  }




  return (
    <ScrollView>
      <SafeAreaView style={styles.appScreen}>

        <View style={styles.formView}>
          <TextInput style={styles.formInput} placeholder='email' 
              value={email} 
              onChangeText={text=>setEmail(text)} />
          <TextInput style={styles.formInput} placeholder='password' 
              value={password} 
              onChangeText={text=>setPassword(text)} />
          <TextInput style={styles.formInput} placeholder='firstname' 
              value={firstName} 
              onChangeText={text=>setFirstName(text)} />
          <TextInput style={styles.formInput} placeholder='lastname' 
              value={lastName} 
              onChangeText={text=>setLastName(text)} />
          <TextInput style={styles.formInput} placeholder='contact' 
              value={contact} 
              onChangeText={text=>setContact(text)} />
          <TextInput style={styles.formInput} placeholder='address' 
              value={address} 
              onChangeText={text=>setAddress(text)} />
          <TouchableOpacity style={styles.formBtn} onPress={SendData}>
            <Text style={styles.formBtnText}>Add Customer</Text>
          </TouchableOpacity>
        </View>


      </SafeAreaView>
    </ScrollView>
  );

}
